prices = {
    'A': 0,
    'B': 1,
    'C': 3,
    'D': 5
}

def solve(input):
    return input.count('B') + input.count('C') * 3

def solve2(input):
    sum = 0
    for pair in zip(input[::2], input[1::2]):
        add = 0 if 'x' in pair else 1
        for c in pair:
            if c in prices:
                sum += prices[c] + add

    return sum

def solve3(input):
    sum = 0
    for triplet in zip(input[::3], input[1::3], input[2::3]):
        add = max(2 - triplet.count('x'), 0)
        for a in triplet:
            if a in prices:
                sum += prices[a] + add

    return sum

print(solve(''))
print(solve2('AxBCDDCAxD'))
print(solve2(''))
print(solve3('xBxAAABCDxCC'))
print(solve3(''))
